import java.util.*;import java.io.*;import java.time.LocalDateTime;import java.time.format.DateTimeFormatter;import java.lang.reflect.*;class TemplateZavod<T> implements AbstractZavod {    private String nazvanieZavoda;    private boolean rabotaet;    private T[] productionQueue;    private int queueIndex;        @SuppressWarnings("unchecked")    public TemplateZavod(String nazvanie, Class<T> clazz) {        this.nazvanieZavoda = nazvanie;        this.rabotaet = false;        this.productionQueue = (T[]) Array.newInstance(clazz, 10);        this.queueIndex = 0;    }        @Override    public void zapustitProizvodstvo() {        rabotaet = true;        if (queueIndex < 10) {            try {                productionQueue[queueIndex++] = null;            } catch (Exception e) {}        }    }        @Override    public void ostanovitProizvodstvo() {        rabotaet = false;        queueIndex = 0;    }        @Override    public String getNazvanieZavoda() { return nazvanieZavoda; }        public void addToQueue(T item) {        if (queueIndex < 10) {            productionQueue[queueIndex++] = item;        }    }        public T[] getProductionQueue() { return productionQueue; }    public int getQueueSize() { return queueIndex; }        public boolean isQueueFull() {        return queueIndex >= 10;    }}